import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
  },
  reducers: {
    addItemHandler(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((el) => el.id === newItem.id);
      if (!existingItem) {
        state.items.push({
          title: newItem.title,
          quantity: 1,
          
        })
      }
    },
  },
});
