import { configureStore, createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
  },
  reducers: {
    addItemHandler(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((el) => el.id === newItem.id);
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          title: newItem.title,
          quantity: 1,
          price: newItem.price,
          totalPrice: newItem.price,
        });
      } else {
        existingItem.quantity++;
        existingItem.totalPrice = existingItem.totalPrice + newItem.price;
      }
    },
    removeItemHandler(state, action) {
      const id = action.payload;
      const existingItem = state.items.find((el) => el.id === id);
      
      if (existingItem) { // Check if item exists
        if (existingItem.quantity === 1) {
          // If quantity is 1, remove the item
          state.items = state.items.filter((el) => el.id !== id);
        } else {
          // If quantity is greater than 1, decrement the quantity
          existingItem.quantity--;
        }
      }
    }
    
  },
});

const store = configureStore({
  reducer: cartSlice.reducer,
});

export const cartActions = cartSlice.actions;

export default store;
