import { useSelector } from "react-redux";
import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import { createSelector } from "@reduxjs/toolkit";

const Cart = (props) => {
  const counterSelector = (state) => state.counter;

  // Create a memoized selector
  const totalPriceSelector = createSelector(
    [counterSelector],
    (counter) => {
      let item = { title: "Test Item", quantity: counter, total: 0, price: 6 };
      item.total = item.price * item.quantity;
      return item;
    }
  );

  // In your component, use the selector
  const totalItem = useSelector(totalPriceSelector);


  // const { counter, showCounter } = useSelector((state) => ({
  //   counter: state.counter,
  //   showCounter: state.showCounter,
  // }));
  // let item = { title: "Test Item", quantity: counter, total: 0, price: 6 };

  // let totalPrice = item.price * item.quantity;
  // item = { ...item, total: totalItem };

  return (
    <>
      {totalItem && (
        <Card className={classes.cart}>
          <h2>Your Shopping Cart</h2>
          <ul>
            <CartItem item={item} />
          </ul>
        </Card>
      )}
    </>
  );
};

export default Cart;
