import { createSlice } from "@reduxjs/toolkit";

const shopSlice = createSlice({
  name: "shop",
  initialState: {
    items: [],
  },
  reducers: {
    addItemsHandler(state, action) {
      const newItem = action.payload;
      
    }
  }
})
