import { useSelector } from "react-redux";
import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import { items } from "../items";

const Cart = (props) => {
  const counter = useSelector((state) => state.counter);
  const items = { title: "Test Item", quantity: counter, total: 0, price: 6 };

  // const totalPrice = items.reduce((acc, el) => acc + el.price * el.quantity);

  // items = [
  //   { title: "Test Item", quantity: counter, total: totalPrice, price: 6 },
  // ];

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        <CartItem item={items} />
      </ul>
    </Card>
  );
};

export default Cart;
